var msj = "Bienvenido al bot del *Edificio Santa Cecilia IX*\r\n";
msj += "\r\n";
msj += "/estado - Este comando es usado para verificar el estado actual de la puerta y del garaje, si estan abiertas o cerradas\r\n";
msj += "\r\n";
msj += "/puerta - Este comando permite que la apertura de la puerta no envie mensaje de alarma al superar el tiempo limite para";
msj += "cancelar la opci칩n se debe cerrar la puerta\r\n";
msj += "\r\n";
msj += "/garaje - Este comando permite que la apertura del garaje no envie mensaje de alarma al superar el tiempo limite para";
msj += "cancelar la opci칩n se debe cerrar el garaje\r\n";
msj += "\r\n";
msj += "/info - muestra este mensaje";

msg.payload.options = {parse_mode : "Markdown"};
msg.payload.content = msj;
return msg;

context.data = context.data || {};

switch (msg.topic) {
    case "stat/nodoscix/puerta":
        context.data.puerta = msg.payload;
        msg = null;
        break;
    case "stat/nodoscix/garaje":
        context.data.garaje = msg.payload;
        msg = null;
        break;
    default:
        msg = null;
    	break;
}

if(context.data.puerta != null && context.data.garaje != null) {
    var msj = context.data;
    context.data=null;
	return {payload:msj};
} else return msg;

var puerta="",garaje="";
if(msg.payload == 00){
    puerta = "*CERRADA*";
    garaje = "*CERRADO*";
}
else if(msg.payload == 01){
    puerta = "*CERRADA*";
    garaje = "*ABIERTO*";
}
else if(msg.payload == 10){
    puerta = "*ABIERTA*";
    garaje = "*CERRADO*";
}
else if(msg.payload == 11){
    puerta = "*ABIERTA*";
    garaje = "*ABIERTO*";
}

var t1p = flow.get('tmpuerta');
var t1g = flow.get('tmgaraje');
var tn = new Date().getTime();
var tpmin = Math.round((tn - t1p)/60000);
var tgmin = Math.round((tn - t1g)/60000);
var thpuerta = Math.floor(tpmin/60);
var thgaraje = Math.floor(tgmin/60);
var tminpuerta = Math.round(((tpmin/60)- thpuerta)*60);
var tmingaraje = Math.round(((tgmin/60)- thgaraje)*60);

var msj = "\u2705*INFORMACION:*\r\n";
msj += "La *PUERTA* est치  "+ puerta +" desde hace " + thpuerta + " horas y " + tminpuerta +" Minutos";
msj += "\r\n";
msj += "El *GARAJE* est치  "+ garaje + " desde hace " + thgaraje + " horas y " + tmingaraje + " Minutos";
msj += "\r\n";
var cid = flow.get('chatid');
msg.payload = {chatId:cid,type:"message",content:msj,options:{parse_mode:"Markdown"}};
return msg;

var est1 = flow.get("estpuerta") || 0;
var est = msg.payload;
flow.set("estpuerta",est);
if(est1 != est){
    var tm = new Date().getTime()
    var tm1 = flow.get('tmpuerta') || 0;
    flow.set('tmpuerta',tm)
    flow.set('tm1puerta',tm1)
    msg.payload = {Time:tm, puerta:msg.payload}
    return msg;
}

var est1 = flow.get("estgaraje") || 0;
var est = msg.payload;
flow.set("estgaraje",est);
if(est1 != est){
    var tm = new Date().getTime()
    var tm1 = flow.get('tmgaraje') || 0;
    flow.set('tmgaraje',tm)
    flow.set('tm1garaje',tm1)
    msg.payload = {Time:tm, garaje:msg.payload}
    return msg;
}

