func:
if(msg.topic =="stat/nodoscix/STATUS5"){
    var ip1 = flow.get('ip1') || "0";
    ip1 = msg.payload.StatusNET.IPAddress;
    flow.set('ip1',ip1);
    msg.payload = {ip:ip1};
    return msg;
}
else if(msg.topic =="stat/nodoscix/STATUS11"){
    var upt = flow.get('upt') || "0";
    var time = flow.get("tim") || "0";
    upt = msg.payload.StatusSTS.Uptime;
    time = msg.payload.StatusSTS.Time;
    flow.set('upt',upt);
    flow.set('tim',time);
    msg.payload = {uptime:upt,timeup:time};
    return msg;
}

var ip1 = flow.get('ip1') || "0";
var upt = flow.get('upt') || "0";
var tim = flow.get('tim') || "0";
var msj = "Hora actualizacion: "+ tim +"\r\nIP: " + ip1 + "\r\nUptime: " + upt ;
msg.payload.content = msj;
return msg;

context.data = context.data || {};
switch (msg.topic) {
    case "tele/nodoscix/STATE":
        var up = flow.get('upt') || "0";
        var time = flow.get("tim") || "0";
        up = msg.payload.Uptime;
        time = msg.payload.Time;
        flow.set('upt',up);
        flow.set('tim',time);
        context.data.upp = up
        context.data.tme = time
        msg = null;
        break;
    case "tele/nodoscix/INFO2":
        context.data.inf1 = msg.payload.Info2.IPAddress;
        msg = null;
        break;
    case "tele/nodoscix/INFO3":
        context.data.inf2 = msg.payload.Info3.RestartReason;
        msg = null;
        break;
    default:
        msg = null;
    	break;
}

if(context.data.inf1 != null && context.data.inf2 != null && context.data.upp != null && context.data.tme != null) {
    var msj = "Hora: "+ context.data.tme +"\r\nUptime: " + context.data.upp +"\r\nIP: " + context.data.inf1 +"\r\nRestart Reason: " + context.data.inf2;
    context.data=null;
    const id = Number(env.get("BOT_IDS").split(",", 2));
    msg = {payload: {chatId:id,type:"message",content:msj,options:{parse_mode:"Markdown"}}};
    return msg;
}

if(msg.payload == "Offline"){
    var tmp = new Date();
    var msj = "Hora Desconexion: "+ tmp +"\r\nEl Nodo se ha *DESCONECTADO* ";
    const id = Number(env.get("BOT_IDS").split(",", 2)[1]);
    msg = {payload: {chatId:id,type:"message",content:msj,options:{parse_mode:"Markdown"}}};
    return msg;
}

if(msg.payload == "Online"){
    var tmp = new Date();
    var msj = "Hora Desconexion: "+ tmp +"\r\nEl Nodo se ha *CONECTADO* ";
    const id = Number(env.get("BOT_IDS").split(",", 2)[1]);
    msg = {payload: {chatId:id,type:"message",content:msj,options:{parse_mode:"Markdown"}}};
    return msg;
}

const id = Number(env.get("BOT_IDS").split(",", 1));
if (msg.payload.chatId === id){
    return [msg,null];
}
else{
    return[null,msg];
}
