function getFormattedUTCTimeMinus5() {
    const now = new Date(new Date().getTime() - 5 * 60 * 60 * 1000);
    return now.toISOString().replace(/\.\d{3}Z$/, 'Z'); // Remove milliseconds and keep the trailing Z
}

